import React from 'react';
import './ProductCard.css';

const ProductCard = ({ product }) => {
  const getPlatformName = (link) => {
    if (link.includes('amazon')) return 'Amazon';
    if (link.includes('flipkart')) return 'Flipkart';
    if (link.includes('croma')) return 'Croma';
    return 'Platform';
  };

  return (
    <div className="product-card">
      <img src={product.image} alt={product.name} className="product-image" />
      <h3 className="product-name">{product.name}</h3>
      <p className="product-price">{product.price}</p>
      <a href={product.link} target="_blank" rel="noopener noreferrer" className="product-link">
        View on {getPlatformName(product.link)}
      </a>
    </div>
  );
};

export default ProductCard;
