import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './ProductComparison.css';

const ProductComparison = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { product } = location.state || {};
  const [isPriceAlertSet, setIsPriceAlertSet] = useState(false);

  // Create an array of all available platforms with their data
  const platforms = [
    {
      name: 'Amazon',
      price: product?.amazonPrice || 'N/A',
      link: product?.amazonLink,
      originalPlatform: product?.platform === 'amazon'
    },
    {
      name: 'Flipkart',
      price: product?.flipkartPrice || 'N/A',
      link: product?.flipkartLink,
      originalPlatform: product?.platform === 'flipkart'
    },
    {
      name: 'Myntra',
      price: product?.myntraPrice || 'N/A',
      link: product?.myntraLink,
      originalPlatform: product?.platform === 'myntra'
    },
    {
      name: 'Nykaa',
      price: product?.nykaaPrice || 'N/A',
      link: product?.nykaaLink,
      originalPlatform: product?.platform === 'nykaa'
    },
    {
      name: 'Croma',
      price: product?.cromaPrice || 'N/A',
      link: product?.cromaLink,
      originalPlatform: product?.platform === 'croma'
    }
  ].filter(p => p.price !== 'N/A'); // Only show platforms with available prices

  // Find the lowest price
  const getNumericPrice = (price) => {
    return parseFloat(price.replace(/[₹,]/g, ''));
  };

  const lowestPricePlatform = platforms.reduce((min, p) => {
    if (p.price === 'N/A') return min;
    return getNumericPrice(p.price) < getNumericPrice(min.price) ? p : min;
  }, platforms[0]);

  const handleSetPriceAlert = () => {
    // Implement price alert logic here
    setIsPriceAlertSet(true);
  };

  const handleBuyNow = (platformUrl) => {
    if (platformUrl) {
      window.open(platformUrl, '_blank');
    }
  };

  if (!product) {
    return <div>No product data available</div>;
  }

  return (
    <div className="comparison-container">
      <button className="back-button" onClick={() => navigate(-1)}>
        ← Back to Search Results
      </button>
      
      <div className="comparison-content">
        <div className="product-image-section">
          <img src={product?.image} alt={product?.name} className="large-product-image" />
        </div>

        <div className="product-details-section">
          <h1 className="product-title">{product?.name}</h1>
          
          <div className="price-comparison">
            <h2>Price Comparison</h2>
            <table className="comparison-table">
              <thead>
                <tr>
                  <th>Platform</th>
                  <th>Price</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {platforms.map((platform) => (
                  <tr key={platform.name} 
                      className={platform.name === lowestPricePlatform?.name ? 'best-price' : ''}>
                    <td>{platform.name}{platform.originalPlatform ? ' (Original)' : ''}</td>
                    <td>{platform.price}</td>
                    <td>
                      {platform.link ? (
                        <button 
                          className={`go-to-store ${platform.name === lowestPricePlatform?.name ? 'glow' : ''}`}
                          onClick={() => handleBuyNow(platform.link)}>
                          Go to {platform.name}
                        </button>
                      ) : (
                        <span className="unavailable">Unavailable</span>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <div className="actions">
            {lowestPricePlatform && (
              <button 
                className="buy-now-button"
                onClick={() => handleBuyNow(lowestPricePlatform.link)}>
                Buy Now from {lowestPricePlatform.name} ({lowestPricePlatform.price})
              </button>
            )}

            <button 
              className={`price-alert-button ${isPriceAlertSet ? 'active' : ''}`}
              onClick={handleSetPriceAlert}>
              {isPriceAlertSet ? 'Price Alert Set!' : 'Set Price Alert'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductComparison;
