import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { searchAllPlatforms } from '../services/api';
import './ProductCard.css';

const ProductCard = ({ product }) => {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);

  const handleViewProduct = async () => {
    try {
      setIsLoading(true);
      
      // Extract the product name for searching
      const searchQuery = product.name.split(' ').slice(0, 3).join(' '); // Use first 3 words for better matches
      console.log('Searching for:', searchQuery);

      // Fetch prices from all platforms
      const allPlatformData = await searchAllPlatforms(searchQuery);
      console.log('Received platform data:', allPlatformData);

      // Get the original platform's data
      const originalPlatform = product.platform || 'unknown';
      const originalPrice = product.price;
      const originalLink = product.link;

      // Combine the data with prices from other platforms
      const combinedData = {
        ...product,
        platform: originalPlatform,
        [`${originalPlatform}Price`]: originalPrice,
        [`${originalPlatform}Link`]: originalLink,
        amazonPrice: allPlatformData.amazon?.[0]?.price || 'N/A',
        amazonLink: allPlatformData.amazon?.[0]?.link || null,
        flipkartPrice: allPlatformData.flipkart?.[0]?.price || 'N/A',
        flipkartLink: allPlatformData.flipkart?.[0]?.link || null,
        myntraPrice: allPlatformData.myntra?.[0]?.price || 'N/A',
        myntraLink: allPlatformData.myntra?.[0]?.link || null,
        nykaaPrice: allPlatformData.nykaa?.[0]?.price || 'N/A',
        nykaaLink: allPlatformData.nykaa?.[0]?.link || null,
        cromaPrice: allPlatformData.croma?.[0]?.price || 'N/A',
        cromaLink: allPlatformData.croma?.[0]?.link || null,
      };

      console.log('Combined data:', combinedData);
      setIsLoading(false);
      navigate('/product-comparison', { state: { product: combinedData } });
    } catch (error) {
      console.error('Error fetching platform data:', error);
      setIsLoading(false);
      // Navigate with current product data if fetch fails
      navigate('/product-comparison', { 
        state: { 
          product: {
            ...product,
            platform: product.platform || 'unknown',
            [`${product.platform}Price`]: product.price,
            [`${product.platform}Link`]: product.link
          } 
        } 
      });
    }
  };

  return (
    <div className="product-card">
      <img src={product.image} alt={product.name} className="product-image" />
      <h3 className="product-name">{product.name}</h3>
      <p className="product-price">{product.price}</p>
      <button 
        onClick={handleViewProduct} 
        className={`product-link ${isLoading ? 'loading' : ''}`}
        disabled={isLoading}
      >
        {isLoading ? 'Loading...' : 'View Product'}
      </button>
    </div>
  );
};

export default ProductCard;
