import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchProducts } from '../services/api';
import ProductCard from '../components/ProductCard';
import FilterBar from '../components/FilterBar';
import './CategoryView.css';

const validPlatforms = ['nykaa', 'amazon', 'flipkart', 'myntra', 'croma', 'zomato', 'swiggy'];

const CategoryView = ({ query }) => {
  const { platform } = useParams();

  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false); // Initially not loading
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({});
  const [comparisonList, setComparisonList] = useState([]);

  const fetchData = async () => {
    if (!validPlatforms.includes(platform)) {
      setError(`Invalid platform: ${platform}. Please select a valid platform.`);
      return;
    }

    setLoading(true);
    setError(null);
    try {
      const data = await fetchProducts(platform, query);
      setProducts(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (key, value) => {
    setFilters((prev) => ({ ...prev, [key]: value }));
  };

  const handleAddToComparison = (product) => {
    setComparisonList((prev) => {
      if (prev.find((item) => item.name === product.name)) {
        return prev; // Avoid duplicates
      }
      return [...prev, product];
    });
  };

  const handleRemoveFromComparison = (productName) => {
    setComparisonList((prev) => prev.filter((item) => item.name !== productName));
  };

  const filteredProducts = products.filter((product) => {
    if (filters.name && !product.name.toLowerCase().includes(filters.name.toLowerCase())) {
      return false;
    }
    if (filters.price && product.price > filters.price) {
      return false;
    }
    if (filters.platform && product.platform !== filters.platform) {
      return false;
    }
    return true;
  });

  const handleMissingData = (product) => {
    return {
      name: product.name || 'Unknown Name',
      rating: product.rating !== 'No rating' ? product.rating : 'Not Rated',
      image: product.image || 'https://via.placeholder.com/100',
      link: product.link || '#',
    };
  };

  const processedProducts = filteredProducts.map(handleMissingData);

  return (
    <div className="category-view">
      <h2>Products for "{query}" on {platform}</h2>
      <FilterBar onFilterChange={handleFilterChange} />
      <button onClick={fetchData} className="search-button">Search</button>
      {loading && <p>Loading...</p>}
      {error && <p>Error: {error}</p>}
      <div className="product-grid">
        {processedProducts.map((product, index) => (
          <ProductCard
            key={index}
            product={product}
            onAddToComparison={() => handleAddToComparison(product)}
          />
        ))}
      </div>
      {comparisonList.length > 0 && (
        <div className="comparison-section">
          <h3>Comparison List</h3>
          <div className="comparison-grid">
            {comparisonList.map((item, index) => (
              <div key={index} className="comparison-card">
                <img src={item.image} alt={item.name} />
                <h4>{item.name}</h4>
                <p>{item.rating}</p>
                <button onClick={() => handleRemoveFromComparison(item.name)}>
                  Remove
                </button>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default CategoryView;
