import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import './ProductComparison.css';

const ProductComparison = () => {
  const [product, setProduct] = useState(null);
  const location = useLocation();

  useEffect(() => {
    const searchParams = new URLSearchParams(location.search);
    const productId = searchParams.get('id');
    
    if (productId) {
      const storedProduct = localStorage.getItem(`productCompare_${productId}`);
      if (storedProduct) {
        setProduct(JSON.parse(storedProduct));
        // Clean up localStorage
        localStorage.removeItem(`productCompare_${productId}`);
      }
    }
  }, [location]);

  const getNumericPrice = (price) => {
    if (!price || price === 'N/A') return Infinity;
    return parseFloat(price.replace(/[₹,]/g, ''));
  };

  if (!product) {
    return <div className="comparison-container">Loading product data...</div>;
  }

  // Create platforms array with only available prices
  const platforms = [
    {
      name: 'Amazon',
      price: product.amazonPrice || 'N/A',
      link: product.amazonLink,
      isOriginal: product.platform === 'amazon'
    },
    {
      name: 'Flipkart',
      price: product.flipkartPrice || 'N/A',
      link: product.flipkartLink,
      isOriginal: product.platform === 'flipkart'
    }
  ].filter(p => p.price !== 'N/A');

  // Find lowest price platform
  const lowestPricePlatform = platforms.reduce((min, p) => {
    return getNumericPrice(p.price) < getNumericPrice(min.price) ? p : min;
  }, platforms[0]);

  const handleBuyNow = (url) => {
    if (url) window.open(url, '_blank');
  };

  return (
    <div className="comparison-container">
      <div className="product-content">
        <div className="product-main">
          <img src={product.image} alt={product.name} className="product-image" />
          <div className="product-info">
            <h1 className="product-title">{product.name}</h1>
            <div className="original-price">
              Original Price: {product.originalPrice}
              {product.platform && <span className="platform-label"> on {product.platform}</span>}
            </div>
          </div>
        </div>

        <div className="price-comparison-section">
          <h2>Available Prices</h2>
          <div className="price-cards">
            {platforms.map((platform) => (
              <div 
                key={platform.name} 
                className={`price-card ${platform === lowestPricePlatform ? 'lowest-price' : ''}`}
              >
                <div className="platform-info">
                  <span className="platform-name">
                    {platform.name}
                    {platform.isOriginal && <span className="original-tag">(Original)</span>}
                  </span>
                  <span className="platform-price">₹{platform.price.replace(/[₹,]/g, '').trim()}</span>
                </div>
                <button 
                  onClick={() => handleBuyNow(platform.link)}
                  className="view-button"
                >
                  VIEW
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductComparison;
