import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './ProductComparison.css';

const ProductComparison = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { product } = location.state || {};
  const [isPriceAlertSet, setIsPriceAlertSet] = useState(false);

  // Create an array of all available platforms with their data
  const platforms = [
    {
      name: 'Amazon',
      price: product?.amazonPrice || 'N/A',
      link: product?.amazonLink,
      originalPlatform: product?.platform === 'amazon'
    },
    {
      name: 'Flipkart',
      price: product?.flipkartPrice || 'N/A',
      link: product?.flipkartLink,
      originalPlatform: product?.platform === 'flipkart'
    },
    {
      name: 'Myntra',
      price: product?.myntraPrice || 'N/A',
      link: product?.myntraLink,
      originalPlatform: product?.platform === 'myntra'
    },
    {
      name: 'Nykaa',
      price: product?.nykaaPrice || 'N/A',
      link: product?.nykaaLink,
      originalPlatform: product?.platform === 'nykaa'
    },
    {
      name: 'Croma',
      price: product?.cromaPrice || 'N/A',
      link: product?.cromaLink,
      originalPlatform: product?.platform === 'croma'
    }
  ].filter(p => p.price !== 'N/A'); // Only show platforms with available prices

  // Find the lowest price
  const getNumericPrice = (price) => {
    return parseFloat(price.replace(/[â‚¹,]/g, ''));
  };

  const lowestPricePlatform = platforms.reduce((min, p) => {
    if (p.price === 'N/A') return min;
    return getNumericPrice(p.price) < getNumericPrice(min.price) ? p : min;
  }, platforms[0]);

  const handleSetPriceAlert = () => {
    // Implement price alert logic here
    setIsPriceAlertSet(true);
  };

  const handleBuyNow = (platformUrl) => {
    if (platformUrl) {
      window.open(platformUrl, '_blank');
    }
  };

  if (!product) {
    return <div>No product data available</div>;
  }

  return (
    <div className="comparison-container">
      <div className="product-header">
        <div className="product-main-info">
          <div className="product-image-section">
            <img src={product?.image} alt={product?.name} className="large-product-image" />
          </div>
          <div className="product-details">
            <h1 className="product-title">{product?.name}</h1>
            <div className="price-overview">
              <h2 className="main-price">â‚¹{lowestPricePlatform?.price?.replace('â‚¹', '').replace(',', '') || 'N/A'}</h2>
              {lowestPricePlatform?.priceChange && (
                <span className={`price-change ${lowestPricePlatform.priceChange > 0 ? 'increase' : 'decrease'}`}>
                  {lowestPricePlatform.priceChange}%
                </span>
              )}
            </div>
            <div className="availability">
              available at {platforms.filter(p => p.price !== 'N/A').length} stores â†’
            </div>
          </div>
        </div>
        <div className="action-buttons">
          <button className="compare-btn" onClick={handleSetPriceAlert}>
            <span className="icon">+</span> Compare
          </button>
          <button className="like-btn">
            <span className="icon">â™¡</span> Like
          </button>
        </div>
      </div>

      <div className="stores-grid">
        {platforms.map((platform) => (
          platform.price !== 'N/A' && (
            <div key={platform.name} className="store-card">
              <div className="store-info">
                <img 
                  src={`/images/${platform.name.toLowerCase()}-logo.png`} 
                  alt={platform.name} 
                  className="store-logo"
                />
                <span className="store-name">{platform.name}</span>
              </div>
              <div className="store-price">â‚¹{platform.price.replace('â‚¹', '').replace(',', '')}</div>
              <a 
                href={platform.link} 
                target="_blank" 
                rel="noopener noreferrer" 
                className="view-button"
              >
                VIEW <img src="/images/store-icon.png" alt="" className="store-icon" />
              </a>
            </div>
          )
        ))}
      </div>

      <div className="product-info">
        <p className="price-info">
          {product?.name} price in India is â‚¹{lowestPricePlatform?.price?.replace('â‚¹', '').replace(',', '') || 'N/A'}. 
          You can get {product?.name} for the lowest price at {lowestPricePlatform?.name}. 
          {product?.name} was last updated on {new Date().toLocaleDateString('en-US', { month: 'long', day: 'numeric', year: 'numeric' })}
          <button className="read-more">Read More</button>
        </p>

        <div className="additional-actions">
          <button className="action-btn">
            <span className="icon">ðŸ“Š</span> Price History
          </button>
          <button className="action-btn">
            <span className="icon">ðŸ””</span> Set Price Alert
          </button>
          <button className="action-btn">
            <span className="icon">ðŸ’°</span> View Prices
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductComparison;
