import React from 'react';
import { useSearchParams } from 'react-router-dom';
import './ProductComparison.css';

const ProductComparison = () => {
  const [searchParams] = useSearchParams();

  // Get product data from URL parameters
  const productData = {
    name: searchParams.get('name'),
    image: searchParams.get('image'),
    originalPrice: searchParams.get('originalPrice'),
    platform: searchParams.get('originalPlatform'),
    amazonPrice: searchParams.get('amazonPrice'),
    amazonLink: searchParams.get('amazonLink'),
    flipkartPrice: searchParams.get('flipkartPrice'),
    flipkartLink: searchParams.get('flipkartLink')
  };

  if (!productData.name) {
    return <div className="comparison-container">No product data available</div>;
  }

  // Create platforms array with only available prices
  const platforms = [
    {
      name: 'Amazon',
      price: productData.amazonPrice,
      link: productData.amazonLink,
      isOriginal: productData.platform === 'amazon'
    },
    {
      name: 'Flipkart',
      price: productData.flipkartPrice,
      link: productData.flipkartLink,
      isOriginal: productData.platform === 'flipkart'
    }
  ].filter(p => p.price && p.price !== 'N/A' && p.link);

  // Find lowest price platform
  const getNumericPrice = (price) => {
    if (!price || price === 'N/A') return Infinity;
    return parseFloat(price.replace(/[₹,]/g, ''));
  };

  const lowestPricePlatform = platforms.reduce((min, p) => {
    return getNumericPrice(p.price) < getNumericPrice(min.price) ? p : min;
  }, platforms[0]);

  const handleBuyNow = (url) => {
    if (url) window.open(url, '_blank');
  };

  return (
    <div className="comparison-container">
      <div className="product-content">
        <div className="product-main">
          <img src={productData.image} alt={productData.name} className="product-image" />
          <div className="product-info">
            <h1 className="product-title">{productData.name}</h1>
            <div className="original-price">
              Original Price: {productData.originalPrice}
              {productData.platform && 
                <span className="platform-label"> on {productData.platform}</span>
              }
            </div>
          </div>
        </div>

        <div className="price-comparison-section">
          <h2>Available Prices</h2>
          <div className="price-cards">
            {platforms.map((platform) => (
              <div 
                key={platform.name} 
                className={`price-card ${platform === lowestPricePlatform ? 'lowest-price' : ''}`}
              >
                <div className="platform-info">
                  <span className="platform-name">
                    {platform.name}
                    {platform.isOriginal && 
                      <span className="original-tag">(Original)</span>
                    }
                  </span>
                  <span className="platform-price">{platform.price}</span>
                </div>
                <button 
                  onClick={() => handleBuyNow(platform.link)}
                  className="view-button"
                >
                  VIEW
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductComparison;
