import React, { useState } from 'react';
import ProductCard from '../components/ProductCard';
import '../pages/CategoryView.css';
import { fetchProducts } from '../services/api';

const Electronics = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [query, setQuery] = useState('');

  const handleSearch = async () => {
    if (!query.trim()) {
      alert('Please enter a search query.');
      return;
    }
    setLoading(true);
    try {
      const response = await fetchProducts('electronics', query.trim());
      setProducts(response);
    } catch (error) {
      console.error('Error fetching electronics data:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="category-view">
      <h1>Search Electronics</h1>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search for electronic products..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button onClick={handleSearch} disabled={loading}>
          {loading ? 'Searching...' : 'Search'}
        </button>
      </div>
      <div className="product-grid">
        {products.map((product, index) => (
          <ProductCard key={index} product={product} />
        ))}
      </div>
    </div>
  );
};

export default Electronics;
